public class MainView extends VerticalLayout {

    final VerticalLayout todosList = new VerticalLayout(); // highlight-line // <1>
    final TextField todoField = new TextField(); // highlight-line // <2>
    final Button addButton = new Button("Add"); // highlight-line // <2>

