public class TodoLayout extends VerticalLayout {

    private final IMediator mediator = new TodoMediator(); // highlight-line

    public TodoLayout() {
        TodoAppEventListener<? extends TodoAppParticipant> actionListener = event -> {
            final TodoAppParticipant todoAppParticipant = event.getSource();
            todoAppParticipant.execute();
        };

        final TodoInput todoInput = new TodoInput(mediator, actionListener); // highlight-line
        final TodoList todoList = new TodoList(mediator, actionListener); // highlight-line

        add(todoInput, todoList);
    }
}
