                undoTodoListElement.addDeleteClickListener(deleteListener); // highlight-line // <3>
                todosList.add(undoTodoListElement); // highlight-line // <3>
            });

            todoDeleteAction.doIt();
            undoStack.add(todoDeleteAction);
        };
    }
