@PWA(name = "Design Pattern Exercise Memento Pattern", shortName = "Memento Pattern Exercise")
public class MainView extends VerticalLayout {

    final VerticalLayout todosList = new VerticalLayout();
    final TextField todoField = new TextField();
    final Button addButton = new Button("Add");

    final Button saveButton = new Button("Save");
    final Button restoreButton = new Button("Restore");
    // <1>
    final TodoRestoreRenderer todoRestoreRenderer = new TodoRestoreRenderer(restoredTodoMessageList -> { // highlight-line
        if (CollectionUtils.isEmpty(restoredTodoMessageList)) { // highlight-line
            return; // highlight-line
        } // highlight-line
        todosList.removeAll(); // highlight-line
        restoredTodoMessageList.stream() // highlight-line
                .forEach(restoredTodoMessage -> { // highlight-line
                    final TodoListElement todoListElement = new TodoListElement(restoredTodoMessage); // highlight-line
                    todoListElement.addDeleteClickListener(deleteClickEvent -> todosList.remove(todoListElement)); // highlight-line
                    todosList.add(todoListElement); // highlight-line
                }); // highlight-line
    }); // highlight-line

    // <2>
    final Originator originator = new Originator(todoRestoreRenderer); // highlight-line
    final LinkedList<Memento> savedStates = new LinkedList<>();

    public MainView() {
        saveButton.addClickListener(event -> {
            savedStates.add(originator.saveToMemento());
        });
        restoreButton.addClickListener(event -> {
            originator.restoreFromMemento(savedStates.getLast());
        });
        addButton.addClickListener(event -> {
            final String todoMessage = todoField.getValue();

            if (StringUtils.hasText(todoMessage)) {
                originator.addState(todoMessage);

                final TodoListElement todoListElement = new TodoListElement(todoMessage);
                todoListElement.addDeleteClickListener(deleteClickEvent -> todosList.remove(todoListElement));
                todosList.add(todoListElement);
                todoField.setValue("");
            }
        });

        add(controlComponent(), todosList);
    }

    private HorizontalLayout controlComponent() { ... }
}


class TodoRestoreRenderer {

    private Consumer<List<String>> renderProcessor;

    public TodoRestoreRenderer(Consumer<List<String>> renderProcessor) {
        this.renderProcessor = renderProcessor;
    }

    public void render(List<String> todoMessageListState) {
        renderProcessor.accept(todoMessageListState);
    }
}

class Originator {

    private final TodoRestoreRenderer todoRestoreRenderer;
    private List<String> todoMessageListState = new ArrayList<>();

    public Originator(TodoRestoreRenderer todoRestoreRenderer) {
        this.todoRestoreRenderer = todoRestoreRenderer;
    }

    public void addState(String todoMessage) {
        this.todoMessageListState.add(todoMessage);
    }

    public Memento saveToMemento() {
        return new Memento(new ArrayList<>(todoMessageListState));
    }

    public void restoreFromMemento(Memento memento) {
        this.todoMessageListState = memento.getSavedMemento();
        todoRestoreRenderer.render(todoMessageListState); // highlight-line // <3>
    }

    private String totoMessageListString(List<String> todoMessageListState) {
        return todoMessageListState.stream()
                .collect(Collectors.joining(","));
    }
}
