import com.vaadin.flow.component.button.Button;
import com.vaadin.flow.component.orderedlayout.HorizontalLayout;
import com.vaadin.flow.component.orderedlayout.VerticalLayout;
import com.vaadin.flow.component.textfield.TextField;
import com.vaadin.flow.router.Route;
import com.vaadin.flow.server.PWA;
import org.springframework.util.StringUtils;
import space.icednut.dev.vaadin.exercise.component.TodoListElement;

@Route
@PWA(name = "Plain Todo App", shortName = "PTA")
public class MainView extends VerticalLayout {

    final VerticalLayout todosList = new VerticalLayout(); // highlight-line // <1>
    final TextField todoField = new TextField(); // highlight-line // <2>
    final Button addButton = new Button("Add"); // highlight-line // <2>

    public MainView() {
        addButton.addClickListener(event -> { // highlight-line // <3>
            final String todoMessage = todoField.getValue();

            if (StringUtils.hasText(todoMessage)) {
                final TodoListElement todoListElement = new TodoListElement(todoMessage); // highlight-line // <4>
                todoListElement.addDeleteClickListener(deleteClickEvent -> todosList.remove(todoListElement));

                todosList.add(todoListElement); // highlight-line // <5>
                todoField.setValue("");
            }
        });

        add(new HorizontalLayout(todoField, addButton), todosList);
    }
}
